#include <stdio.h> #include <stdlib.h> #define max 100  typedef char ElemType;    typedef struct BiTNode{        ElemType   data;       struct BiTNode  *lchild,*rchild;    } BiTNode,*BinTree;  //建立二叉树  void CreateBinTree(BinTree &T){    char ch;  ch=getchar();  if(ch==' ') T=NULL; else{  if(!(T=(BiTNode *)malloc(sizeof(BiTNode)))) printf("%c" "结点建立失败!") ; T->data=ch;  CreateBinTree(T->lchild);  CreateBinTree(T->rchild);  }  }    //遍历二叉树 
